		END IF;
        
		IF @SetId != 10
		THEN
			-- SQLINES LICENSE FOR EVALUATION USE ONLY
			SELECT MIN(ReservePersonScoreRowId) INTO @ReservePersonScoreRowId
			FROM bowling.ReservePersonScoreRow
			WHERE ReserveId = @ReserveId
			AND IsDone = 0
			AND SetId = @SetId;

			-- SQLINES LICENSE FOR EVALUATION USE ONLY
			SELECT Ball1Score, ReservePersonScoreId 
            INTO @Score1, @ReservePersonScoreId
			FROM bowling.ReservePersonScoreRow
			WHERE ReservePersonScoreRowId = @ReservePersonScoreRowId;

			IF IFNULL(Trim(@Score1),'') = '' 
			THEN
				UPDATE bowling.ReservePersonScoreRow
				SET Ball1Score = @Score, IsDone = CASE WHEN @Score = 10 THEN 1 ELSE 0 End
				WHERE ReservePersonScoreRowId = @ReservePersonScoreRowId;
				IF @Score = 10 THEN
					SET @PLCCommand = 3;
				Elseif @Score <= 1 Then
					Set @PLCCommand = 2;
				Else
					SET @PLCCommand = 1;
				END IF;
			ELSE
				UPDATE bowling.ReservePersonScoreRow
				SET Ball2Score = CASE When @Score - CAST(Ball1Score AS SIGNED) > 0 THEN @Score - CAST(Ball1Score  As SIGNED) ELSE 0 END, IsDone = 1
				WHERE ReservePersonScoreRowId = @ReservePersonScoreRowId;
