CREATE DEFINER=`root`@`localhost` PROCEDURE `ReservePersonRollScoreTrigger`(
	IN RPSId Int,
    IN RlId Int
)
BEGIN
	SET SQL_SAFE_UPDATES = 0;
    Set @ReservePersonScoreId = RPSId;
    Set @RollId = RlId;
 	Set @cnt = 1;
	Set @IsSpare = 0;
	Set @IsStrike = 0;
	Set @IsLastFrame = 0;
	Set @Score = 0;
	Set @Sum = 0;

	WHILE (@cnt <= @RollId)
	DO
		SELECT IsSpare,
				IsStrike,
				IsLastFrame,
				Score INTO @IsSpare, @IsStrike, @IsLastFrame, @Score
		FROM bowling.ReservePersonRollScore
		WHERE ReservePersonRollScore.ReservePersonScoreId = @ReservePersonScoreId 		
		AND ReservePersonRollScore.RollId = @cnt;
        
		IF (@IsStrike = 1 AND NOT(@IsLastFrame = 1))
		THEN
			SET @Sum = @Sum + (
								SELECT SUM(Score)
								FROM bowling.ReservePersonRollScore
                                Inner Join (  SELECT RollId
												FROM bowling.ReservePersonRollScore
												WHERE IFNULL(Score,-1) != -1
												AND ReservePersonScoreId = @ReservePersonScoreId
												AND RollId >= @cnt
                                                Limit 3) vr On ReservePersonRollScore.RollId = vr.RollId
								WHERE ReservePersonScoreId = @ReservePersonScoreId 		
								
								);
				SET @cnt = @cnt + 1;
            ELSEIF (@IsSpare = 1 AND NOT(@IsLastFrame = 1))
			THEN
				SET @Sum = @Sum + (
								SELECT SUM(Score)
								FROM bowling.ReservePersonRollScore
                                Inner Join (SELECT RollId
												FROM bowling.ReservePersonRollScore
												WHERE IFNULL(Score,-1) != -1
												AND ReservePersonScoreId = @ReservePersonScoreId
												AND RollId >= @cnt
                                                Limit 2) vr2 on ReservePersonRollScore.RollId = vr2.RollId
								WHERE ReservePersonScoreId = @ReservePersonScoreId 		
								);
			ELSE
				SET @Sum = @Sum + Ifnull(@Score,0);
			END IF;
			IF @cnt = 1 OR @cnt = 2 
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set1Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 3 OR @cnt = 4
