				SET Set3Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 7 OR @cnt = 8
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set4Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;

			END IF;
			IF @cnt = 9 OR @cnt = 10 
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set5Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 11 OR @cnt = 12
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set6Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 13 OR @cnt = 14
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set7Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 15 OR @cnt = 16
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set8Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 17 OR @cnt = 18
			THEN
				UPDATE bowling.ReservePersonScore
				SET Set9Score = @Sum
				WHERE ReservePersonScoreId = @ReservePersonScoreId;
			END IF;
			IF @cnt = 19 OR @cnt = 20 OR @cnt = 21
