CREATE DEFINER=`root`@`localhost` PROCEDURE `SetCurrentPlayer`(IN resId Int)
BEGIN
	Set @ReserveId = resId;
	Update bowling.reservepersonscore
	Set IsMaxScore = 0
	Where reserveId = @reserveId;

	with cte as
	(
	select reservepersonscore.ReservePersonScoreId, Max(RollId) RollId
	from bowling.reservepersonrollscore
	inner join bowling.reservepersonscore on reservepersonrollscore.ReservePersonScoreId = reservepersonscore.ReservePersonScoreId
	where reserveid = @reserveId
	And rollid mod 2 = 0
	group by reservepersonscore.ReservePersonScoreId
	order by rollId
	), cte2 as
	(
		Select  Min(RollId) RollId
		From cte
	), cte3 as
	(
		Select Min(ReservePersonScoreId) ReservePersonScoreId
		From cte
		inner join cte2 on cte.rollId = cte2.rollid
	)
	Update bowling.reservepersonscore
	Set IsMaxScore = 1
	Where ReservePersonScoreId = (
	Select ReservePersonScoreId
	from cte3
	);
END ;;
