CREATE DEFINER=`root`@`localhost` PROCEDURE `SP_InsertScore`(
	IN RPSId int,
	IN Scr int,
    IN FScr int
)
BEGIN
	Set @ReservePersonScoreId = RPSId;
    Set @Score = Scr;
    Set @FullScore = FScr;
    Set @StatusVal = 0;
    
    Select PersonGender, LineId Into @PersonGender, @LineId
    From bowling.reservepersonscore
    INNER JOIN bowling.reserve On reservepersonscore.reserveId = reserve.reserveId
    Where ReservePersonScoreId = @ReservePersonScoreId;
    if @PersonGender = 1 Then
		Set @StatusVal = 5;
	Else		
		Set @StatusVal = CASE WHEN @RollId % 2 = 1 AND @FullScore = 10 THEN 1 
							  WHEN @RollId % 2 = 0 AND @FullScore = 10 THEN 2
                              WHEN @Score = 0 THEN 3 
                              ELSE 4 END;
    End if;
    Update bowling.plccommand
    Set PORTNumber = @StatusVal
    Where LineId = @LineId;
    
    SELECT IFNULL(MAX(RollId), 0) + 1 INTO @RollId
	FROM bowling.ReservePersonRollScore
	WHERE ReservePersonScoreId = @ReservePersonScoreId;
    
	SET @RollId = CASE WHEN IFNULL(@RollId, 0) = 0 THEN 1 ELSE @RollId END;

	INSERT INTO bowling.ReservePersonRollScore
		(`ReservePersonScoreId`
		,`RollId`
		,`Score`
		,`IsStrike`
		,`IsSpare`
		,`IsLastFrame`)
	VALUES
		(@ReservePersonScoreId
		,@RollId
		,@Score
		,CASE WHEN @RollId % 2 = 1 AND @FullScore = 10 THEN 1 ELSE 0 END
		,CASE WHEN @RollId % 2 = 0 AND @FullScore = 10 THEN 1 ELSE 0 END
		,CASE WHEN @RollId >= 19 THEN 1 ELSE 0 END);

	IF @Score = 10 AND @RollId % 2 = 1 AND @RollId != 19
	THEN
		INSERT INTO bowling.ReservePersonRollScore
				(`ReservePersonScoreId`
				,`RollId`
				,`Score`
				,`IsStrike`
				,`IsSpare`
				,`IsLastFrame`)
			VALUES
				(@ReservePersonScoreId
				,@RollId + 1
				,NULL
				,0
				,0
				,CASE WHEN @RollId >= 19 THEN 1 ELSE 0 END);
    End if;
    
END ;;
